import path from 'path';
import { isAbsolute, join } from 'node:path';
import type { MergeArgs, ProcessedMergeArgs } from './types';

/**
 * Process and validate CLI arguments for the merge command
 */
export function processArgs(argv: MergeArgs): ProcessedMergeArgs {
  let { inputDir, outputDir, verbose } = argv;

  if (!inputDir) {
    // Should be caught by yargs due to <inputDir> being required (demandOption)
    // and builder.ts check
    throw new Error('Input directory is required and was not provided.');
  }

  const resolvedInputDir = isAbsolute(inputDir)
    ? inputDir
    : join(process.cwd(), inputDir);

  let resolvedOutputDir: string;
  if (!outputDir) {
    resolvedOutputDir = join(resolvedInputDir, 'merged-profile.json');
  } else {
    // If outputDir is provided, resolve it relative to cwd if it's not absolute
    resolvedOutputDir = isAbsolute(outputDir)
      ? outputDir
      : join(process.cwd(), outputDir);
  }

  // outputDir is a file path, not a directory path for the output.
  // The actual directory where the file will be saved is derived from it.
  const actualOutputDirectoryPath = path.dirname(resolvedOutputDir);

  if (verbose) {
    console.log(` CPU Profile Merge Mode:`);
    console.log(`   Input directory: ${resolvedInputDir}`);
    console.log(`   Output file path: ${resolvedOutputDir}`);
    console.log(`  mkdir -p for: ${actualOutputDirectoryPath}`);
  }

  return {
    inputDir: resolvedInputDir,
    outputDir: resolvedOutputDir,
    verbose: verbose || false,
  };
}

import type { Argv } from 'yargs';
import {
  fileExists,
  isJsonFile,
  directoryExists,
} from '../../../lib/file-utils';
import type { MergeArgs } from './types';

/**
 * Build the yargs command configuration for merge
 */
export function builder(yargs: Argv): Argv<MergeArgs> {
  return yargs
    .positional('inputDir', {
      describe: 'Directory containing CPU profile files to merge',
      type: 'string',
      normalize: true,
      demandOption: true,
    })
    .group(['help', 'verbose', 'output'], 'Basic Options:')
    .option('outputDir', {
      alias: 'o',
      describe:
        'Output file path for merged trace. Defaults to inputDir/merged-profile.json if not specified.',
      type: 'string',
      normalize: true,
    })
    .option('verbose', {
      alias: 'v',
      describe: 'Enable verbose logging',
      type: 'boolean',
      default: false,
    })

    .example(
      '$0 cpu-merge ./path/to/profiles',
      'Merge all profiles from a directory'
    )
    .epilog(``)

    .check((argv) => {
      const inputDirectory = argv.inputDir as string | undefined;

      if (!inputDirectory) {
        throw new Error('Input directory is required.');
      }

      if (inputDirectory && !directoryExists(inputDirectory)) {
        throw new Error(`Input directory does not exist: ${inputDirectory}`);
      }

      return true;
    });
}

import type { CommandModule } from 'yargs';
import type { MergeArgs } from './types';
import { builder } from './builder';
import { handler } from './handler';

/**
 * Merge command module for yargs
 */
export const mergeCommand: CommandModule<{}, MergeArgs> = {
  command: 'cpu-merge <inputDir>',
  describe:
    'Merge multiple Chrome DevTools trace files or CPU profile files into a single file',
  builder,
  handler,
};

export default mergeCommand;

// Re-export types and utilities for external use
export type { MergeArgs, ProcessedMergeArgs } from './types';
export { processArgs } from './args-processor';

import type { ReduceTraceArgs, ProcessedArgs } from './types';

/**
 * Process and validate CLI arguments for the reduce-trace command
 * Note: Default input/output file handling is now done by yargs middleware
 */
export function processArgs(
  argv: ReduceTraceArgs,
  logger = console
): ProcessedArgs {
  // Input and output files are now handled by middleware
  const inputFile = argv.inputFile!; // Safe to assert since middleware ensures it's set
  const outputFile = argv.output!; // Safe to assert since middleware ensures it's set

  if (argv.verbose) {
    // Verbose logging is handled elsewhere - this just indicates middleware was used
    logger.log(` Using input file: ${inputFile}`);
    logger.log(` Using output file: ${outputFile}`);
  }

  return {
    inputFile,
    outputFile,
    verbose: argv.verbose || false,
    filterOptions: {
      filterNetwork: argv.network ?? true,
      filterAnimation: argv.animation ?? true,
      filterGPU: argv.gpu ?? true,
      filterThreadPool: argv.threadpool ?? true,
      filterStreamingCompile: argv.streamingcompile ?? true,
      durMin: argv['dur-min'],
      durMax: argv['dur-max'],
      tsMin: argv['ts-min'],
      tsMax: argv['ts-max'],
      includePhases: argv['include-phases'],
      excludePhases: argv['exclude-phases'],
      includePids: argv['include-pids'],
      excludePids: argv['exclude-pids'],
      includeTids: argv['include-tids'],
      excludeTids: argv['exclude-tids'],
      includeNames: argv['include-names'],
      excludeNames: argv['exclude-names'],
      includeCats: argv['include-cats'],
      excludeCats: argv['exclude-cats'],
    },
  };
}

import type { Argv } from 'yargs';
import { DEFAULT_FILTER_OPTIONS } from '../../../lib/reduce-trace';
import type { ReduceTraceArgs } from './types';
import {
  coerceStringArray,
  coerceNumberArray,
  coerceStringArrayWithDefaults,
} from '../../../lib/utils';
import { validateTraceReduceArgs } from './helpers';
import { findNewestTraceFile, generateOutputFilename } from '../../utils';

// Constants for default values to reduce duplication
const DEFAULT_DUR_MIN = DEFAULT_FILTER_OPTIONS.durMin;
const DEFAULT_EXCLUDE_NAMES =
  DEFAULT_FILTER_OPTIONS.excludeNames?.join(', ') ?? '';
const DEFAULT_EXCLUDE_CATS =
  DEFAULT_FILTER_OPTIONS.excludeCats?.join(', ') ?? '';
const DEFAULT_EXCLUDE_NAMES_COMMA =
  DEFAULT_FILTER_OPTIONS.excludeNames?.join(',') ?? '';
const DEFAULT_EXCLUDE_CATS_COMMA =
  DEFAULT_FILTER_OPTIONS.excludeCats?.join(',') ?? '';

/**
 * Add basic options (output, verbose)
 */
function addBasicOptions(yargs: Argv<ReduceTraceArgs>): Argv<ReduceTraceArgs> {
  return yargs
    .group(['help', 'version', 'verbose', 'output'], 'Basic Options:')
    .options({
      output: {
        alias: 'o',
        describe: 'Output file path',
        type: 'string',
        normalize: true,
      },
      verbose: {
        alias: 'v',
        describe: 'Enable verbose logging',
        type: 'boolean',
        default: false,
      },
    });
}

/**
 * Add event type filtering options
 */
function addEventTypeFilters(
  yargs: Argv<ReduceTraceArgs>
): Argv<ReduceTraceArgs> {
  return yargs
    .group(
      ['network', 'animation', 'gpu', 'threadpool', 'streamingcompile'],
      'Event Type Filtering:'
    )
    .options({
      network: {
        describe: 'Enable network event filtering',
        type: 'boolean',
        default: true,
      },
      animation: {
        describe: 'Enable animation event filtering',
        type: 'boolean',
        default: true,
      },
      gpu: {
        describe: 'Enable GPU event filtering',
        type: 'boolean',
        default: true,
      },
      threadpool: {
        describe: 'Enable ThreadPool event filtering',
        type: 'boolean',
        default: true,
      },
      streamingcompile: {
        describe: 'Enable StreamingCompile event filtering',
        type: 'boolean',
        default: true,
      },
    });
}

/**
 * Add advanced filtering options (phases, PIDs, TIDs)
 */
function addAdvancedFiltering(
  yargs: Argv<ReduceTraceArgs>
): Argv<ReduceTraceArgs> {
  return yargs
    .group(
      [
        'include-phases',
        'exclude-phases',
        'include-pids',
        'exclude-pids',
        'include-tids',
        'exclude-tids',
      ],
      'Advanced Filtering:'
    )
    .options({
      'include-phases': {
        describe:
          'Comma-separated list of event phases to include (e.g., B,E,X)',
        type: 'array',
        coerce: coerceStringArray,
      },
      'exclude-phases': {
        describe: 'Comma-separated list of event phases to exclude (e.g., M,I)',
        type: 'array',
        coerce: coerceStringArray,
      },
      'include-pids': {
        describe: 'Comma-separated list of Process IDs to include',
        type: 'array',
        coerce: (arg) => coerceNumberArray(arg, 'PID'),
      },
      'exclude-pids': {
        describe: 'Comma-separated list of Process IDs to exclude',
        type: 'array',
        coerce: (arg) => coerceNumberArray(arg, 'PID'),
      },
      'include-tids': {
        describe: 'Comma-separated list of Thread IDs to include',
        type: 'array',
        coerce: (arg) => coerceNumberArray(arg, 'TID'),
      },
      'exclude-tids': {
        describe: 'Comma-separated list of Thread IDs to exclude',
        type: 'array',
        coerce: (arg) => coerceNumberArray(arg, 'TID'),
      },
    });
}

/**
 * Add name and category filtering options
 */
function addNameCatFiltering(
  yargs: Argv<ReduceTraceArgs>
): Argv<ReduceTraceArgs> {
  return yargs
    .group(
      ['include-names', 'exclude-names', 'include-cats', 'exclude-cats'],
      'Name & Category Filtering:'
    )
    .options({
      'include-names': {
        describe: 'Comma-separated list of event names to include',
        type: 'array',
        coerce: coerceStringArray,
      },
      'exclude-names': {
        describe: `Comma-separated list of event names to exclude (default: ${DEFAULT_EXCLUDE_NAMES_COMMA})`,
        type: 'array',
        default: DEFAULT_FILTER_OPTIONS.excludeNames,
        coerce: (arg) =>
          coerceStringArrayWithDefaults(
            arg,
            DEFAULT_FILTER_OPTIONS.excludeNames
          ),
      },
      'include-cats': {
        describe: 'Comma-separated list of event categories to include',
        type: 'array',
        coerce: coerceStringArray,
      },
      'exclude-cats': {
        describe: `Comma-separated list of event categories to exclude (default: ${DEFAULT_EXCLUDE_CATS_COMMA})`,
        type: 'array',
        default: DEFAULT_FILTER_OPTIONS.excludeCats,
        coerce: (arg) =>
          coerceStringArrayWithDefaults(
            arg,
            DEFAULT_FILTER_OPTIONS.excludeCats
          ),
      },
    });
}

/**
 * Add duration and timestamp filtering options
 */
function addDurationTimestampFiltering(
  yargs: Argv<ReduceTraceArgs>
): Argv<ReduceTraceArgs> {
  return yargs
    .group(
      ['dur-min', 'dur-max', 'ts-min', 'ts-max'],
      'Duration & Timestamp Filtering:'
    )
    .options({
      'dur-min': {
        describe: `Filter out events shorter than specified duration in microseconds (default: ${DEFAULT_DUR_MIN}s)`,
        type: 'number',
        default: DEFAULT_FILTER_OPTIONS.durMin,
      },
      'dur-max': {
        describe:
          'Filter out events longer than specified duration in microseconds',
        type: 'number',
      },
      'ts-min': {
        describe:
          'Filter out events with timestamp earlier than specified in microseconds',
        type: 'number',
      },
      'ts-max': {
        describe:
          'Filter out events with timestamp later than specified in microseconds',
        type: 'number',
      },
    });
}

/**
 * Build the yargs command configuration for reduce-trace
 */
export function builder(yargs: Argv): Argv<ReduceTraceArgs> {
  return [
    addBasicOptions,
    addEventTypeFilters,
    addAdvancedFiltering,
    addNameCatFiltering,
    addDurationTimestampFiltering,
  ]
    .reduce((acc, fn) => fn(acc), yargs)
    .positional('inputFile', {
      describe: 'Path to the input trace file (JSON format)',
      type: 'string',
      normalize: true,
    })
    .example(
      '$0 trace-reduce trace.json',
      'Reduce trace.json with default filters'
    )
    .example(
      '$0 trace-reduce trace.json -o cleaned.json -v',
      'Reduce with custom output and verbose logging'
    )
    .example(
      '$0 trace-reduce --no-network --no-gpu trace.json',
      'Reduce keeping network and GPU events'
    )
    .example(
      '$0 trace-reduce --include-cats blink,v8 trace.json',
      'Include only blink and v8 categories'
    )
    .example(
      '$0 trace-reduce --dur-min 1000 --dur-max 50000 trace.json',
      'Filter by duration range'
    )
    .epilog(
      `
Default Behavior:
   Network, animation, GPU, ThreadPool, StreamingCompile filtering: enabled
   v8.compile category filtering: enabled  
   Minimum duration filtering: ${DEFAULT_DUR_MIN}s
   Default excluded names: ${DEFAULT_EXCLUDE_NAMES}
   If no input file provided, uses newest .json file from ./packages/cpu-profiling/mocks/fixtures/
   If no output file specified, adds .reduced.json to the input filename
   Use --no-<option> to disable any boolean filter (e.g., --no-network, --no-animation)
   Use --no-dur-min to disable minimum duration filtering
    `
    )
    .conflicts('include-phases', 'exclude-phases')
    .conflicts('include-pids', 'exclude-pids')
    .conflicts('include-tids', 'exclude-tids')
    .conflicts('include-names', 'exclude-names')
    .conflicts('include-cats', 'exclude-cats')
    .middleware((argv) => {
      // Handle input file (auto-select if not provided)
      if (!argv.inputFile) {
        argv.inputFile = findNewestTraceFile();
      }

      // Handle output file (auto-generate if not provided)
      if (!argv.output) {
        argv.output = generateOutputFilename(argv.inputFile);
      }
    })
    .check(validateTraceReduceArgs);
}

import fs from 'fs';
import { reduceTrace, reduceTraceFile } from '../../../lib/reduce-trace';
import type { ReduceTraceArgs } from './types';
import { processArgs } from './args-processor';
import { logVerboseOptions, getStats, logStats } from './helpers';
/**
 * Handle the reduce-trace command execution
 */
export async function handler(argv: ReduceTraceArgs): Promise<void> {
  try {
    const processedArgs = processArgs(argv);

    if (processedArgs.verbose) {
      logVerboseOptions(processedArgs);
    }

    // Use helper function to process the trace file
    const result = reduceTraceFile(
      processedArgs.inputFile,
      processedArgs.outputFile,
      processedArgs.filterOptions
    );

    // Display statistics using helper functions
    const stats = getStats(result);
    logStats(stats);
  } catch (error) {
    console.error(' Error:', (error as Error).message);
    process.exit(1);
  }
}

import type { ProcessedArgs } from './types';
import type { TraceReductionStats } from '../../../lib/reduce-trace';
import path from 'path';
import {
  fileExists,
  isJsonFile,
  directoryExists,
} from '../../../lib/file-utils';

/**
 * Log verbose options when verbose mode is enabled
 */
export function logVerboseOptions(args: ProcessedArgs, logger = console): void {
  logger.log(' Filter Options:');
  logger.log(
    `  Network filtering: ${
      args.filterOptions.filterNetwork ? 'enabled' : 'disabled'
    }`
  );
  logger.log(
    `  Animation filtering: ${
      args.filterOptions.filterAnimation ? 'enabled' : 'disabled'
    }`
  );
  logger.log(
    `  GPU filtering: ${args.filterOptions.filterGPU ? 'enabled' : 'disabled'}`
  );
  logger.log(
    `  ThreadPool filtering: ${
      args.filterOptions.filterThreadPool ? 'enabled' : 'disabled'
    }`
  );
  logger.log(
    `  StreamingCompile filtering: ${
      args.filterOptions.filterStreamingCompile ? 'enabled' : 'disabled'
    }`
  );

  if (args.filterOptions.durMin !== undefined) {
    logger.log(`  Minimum duration: ${args.filterOptions.durMin}s`);
  }
  if (args.filterOptions.durMax !== undefined) {
    logger.log(`  Maximum duration: ${args.filterOptions.durMax}s`);
  }
  if (args.filterOptions.tsMin !== undefined) {
    logger.log(`  Minimum timestamp: ${args.filterOptions.tsMin}`);
  }
  if (args.filterOptions.tsMax !== undefined) {
    logger.log(`  Maximum timestamp: ${args.filterOptions.tsMax}`);
  }
  if (args.filterOptions.includePhases) {
    logger.log(
      `  Include phases: ${args.filterOptions.includePhases.join(', ')}`
    );
  }
  if (args.filterOptions.excludePhases) {
    logger.log(
      `  Exclude phases: ${args.filterOptions.excludePhases.join(', ')}`
    );
  }
  if (args.filterOptions.includePids) {
    logger.log(`  Include PIDs: ${args.filterOptions.includePids.join(', ')}`);
  }
  if (args.filterOptions.excludePids) {
    logger.log(`  Exclude PIDs: ${args.filterOptions.excludePids.join(', ')}`);
  }
  if (args.filterOptions.includeTids) {
    logger.log(`  Include TIDs: ${args.filterOptions.includeTids.join(', ')}`);
  }
  if (args.filterOptions.excludeTids) {
    logger.log(`  Exclude TIDs: ${args.filterOptions.excludeTids.join(', ')}`);
  }
  if (args.filterOptions.includeNames) {
    logger.log(
      `  Include names: ${args.filterOptions.includeNames.join(', ')}`
    );
  }
  if (args.filterOptions.excludeNames) {
    logger.log(
      `  Exclude names: ${args.filterOptions.excludeNames.join(', ')}`
    );
  }
  if (args.filterOptions.includeCats) {
    logger.log(
      `  Include categories: ${args.filterOptions.includeCats.join(', ')}`
    );
  }
  if (args.filterOptions.excludeCats) {
    logger.log(
      `  Exclude categories: ${args.filterOptions.excludeCats.join(', ')}`
    );
  }

  logger.log(` Input file: ${args.inputFile}`);
  logger.log(` Output file: ${args.outputFile}`);
}

/**
 * Validate command line arguments for trace-reduce command
 */
export function validateTraceReduceArgs(argv: any): boolean {
  // File validation
  const inputFile = argv.inputFile;
  if (inputFile) {
    if (!fileExists(inputFile as string)) {
      throw new Error(`Input file does not exist: ${inputFile}`);
    }
    if (!isJsonFile(inputFile as string)) {
      throw new Error(`Input file must be a JSON file: ${inputFile}`);
    }
  }

  // Output directory validation
  if (argv.output) {
    const outputDir = path.dirname(argv.output as string);
    if (outputDir !== '.' && !directoryExists(outputDir)) {
      throw new Error(`Output directory does not exist: ${outputDir}`);
    }
  }

  // Duration min/max relationship validation
  if (
    argv['dur-min'] !== undefined &&
    argv['dur-max'] !== undefined &&
    argv['dur-min'] > argv['dur-max']
  ) {
    throw new Error('--dur-min cannot be greater than --dur-max');
  }

  // Timestamp min/max relationship validation
  if (
    argv['ts-min'] !== undefined &&
    argv['ts-max'] !== undefined &&
    argv['ts-min'] > argv['ts-max']
  ) {
    throw new Error('--ts-min cannot be greater than --ts-max');
  }

  return true;
}

/**
 * Format trace reduction statistics for display
 */
export interface FormattedStats {
  originalFileMB: string;
  originalEventCount: number;
  cleanedFileMB: string;
  filteredEventCount: number;
  removedEventCount: number;
  reductionRatio: string;
  inputFile: string;
  outputFile: string;
}

export function getStats(result: TraceReductionStats): FormattedStats {
  return {
    originalFileMB: (result.originalSize / (1024 * 1024)).toFixed(2),
    originalEventCount: result.originalEventCount,
    cleanedFileMB: (result.cleanedSize / (1024 * 1024)).toFixed(2),
    filteredEventCount: result.filteredEventCount,
    removedEventCount: result.removedEventCount,
    reductionRatio: (
      (result.removedEventCount / result.originalEventCount) *
      100
    ).toFixed(1),
    inputFile: result.inputFile,
    outputFile: result.outputFile,
  };
}

/**
 * Log trace reduction statistics to console
 */
export function logStats(stats: FormattedStats, logger = console): void {
  logger.log(
    ` Original file: ${stats.originalFileMB} MB, ${stats.originalEventCount} events`
  );
  logger.log(
    ` Cleaned file: ${stats.cleanedFileMB} MB, ${stats.filteredEventCount} events`
  );
  logger.log(` Events removed: ${stats.removedEventCount}`);
  logger.log(` Reduction ratio: ${stats.reductionRatio}%`);
  logger.log(` Reduced trace file created: ${stats.outputFile}`);
  logger.log(` Original file preserved: ${stats.inputFile}`);
}

import type { CommandModule } from 'yargs';
import type { ReduceTraceArgs } from './types';
import { builder } from './builder';
import { handler } from './handler';

export const reduceTraceCommand: CommandModule<{}, ReduceTraceArgs> = {
  command: 'trace-reduce [inputFile]',
  describe: 'Reduce Chrome DevTools trace files by filtering unwanted events',
  builder,
  handler,
};

export default reduceTraceCommand;

export type { ReduceTraceArgs, ProcessedArgs, ValidatedArgs } from './types';
export { processArgs } from './args-processor';
export { logVerboseOptions } from './helpers';

#!/usr/bin/env node
import { fileURLToPath } from 'url';
