    it('should extract thread pool threads from metadata events', () => {
      const events: TraceEvent[] = [
        {
          ph: 'M',
          name: 'thread_name',
          args: { name: 'ThreadPoolBackgroundWorker' },
          pid: 1,
          tid: 100,
        } as unknown as ThreadNameEvent,
