            } as unknown as CompleteEvent,
            {
              name: 'Regular',
            } as unknown as CompleteEvent,
          ],
          { filterGPU: true }
        )
      ).toStrictEqual([expect.objectContaining({ name: 'Regular' })]);
    });

    it('should filter thread pool events when filterThreadPool is true', () => {
      expect(
        filterTraceEvents(
          [
            {
              ph: 'M',
              name: 'thread_name',
              args: { name: 'worker' },
              pid: 1,
              tid: 100,
            } as unknown as CompleteEvent,
            {
              pid: 1,
              tid: 100,
            } as unknown as CompleteEvent,
            {
              pid: 1,
              tid: 1,
            } as unknown as CompleteEvent,
          ],
          { filterThreadPool: true }
        )
      ).toStrictEqual([
        expect.objectContaining({ ph: 'M' }),
        expect.objectContaining({ pid: 1, tid: 1 }),
      ]);
    });

    it('should filter streaming compile events when filterStreamingCompile is true', () => {
      expect(
        filterTraceEvents(
          [
            {
              name: 'StreamCompileTask',
            } as unknown as CompleteEvent,
            {
              name: 'Regular',
            } as unknown as CompleteEvent,
          ],
          { filterStreamingCompile: true }
        )
      ).toStrictEqual([expect.objectContaining({ name: 'Regular' })]);
    });

    it('should filter by duration when durMin is provided', () => {
      expect(
        filterTraceEvents(
          [
            {
              name: 'ShortDuration',
              dur: 5000,
            } as unknown as CompleteEvent,
            {
              name: 'LongDuration',
              dur: 15000,
            } as unknown as CompleteEvent,
          ],
          { durMin: 10000 }
        )
      ).toStrictEqual([expect.objectContaining({ name: 'LongDuration' })]);
    });

    it('should return all events when no filters are applied', () => {
      expect(
        filterTraceEvents(
          [
            { name: 'Event1' } as unknown as CompleteEvent,
            { name: 'Event2' } as unknown as CompleteEvent,
          ],
          {}
        )
      ).toStrictEqual([
        expect.objectContaining({ name: 'Event1' }),
        expect.objectContaining({ name: 'Event2' }),
      ]);
    });
  });

  describe('reduceTrace', () => {
    it('should reduce trace data and return filtered data with stats', () => {
      const result = reduceTrace(
        JSON.stringify({
          traceEvents: [
            { name: 'Regular' },
            { name: 'ResourceSendRequest', cat: 'netlog' },
            { name: 'Animation', cat: 'blink.animations' },
          ],
          displayTimeUnit: 'ms',
        }),
        {
          filterNetwork: true,
          filterAnimation: true,
        }
      );

      expect(result.stats.originalEventCount).toBe(3);
      expect(result.stats.filteredEventCount).toBe(1);
      expect(result.stats.removedEventCount).toBe(2);
      expect(result.stats.originalSize).toBeGreaterThan(0);
      expect(result.stats.cleanedSize).toBeGreaterThan(0);
      expect(result.filteredTraceData).toContain('Regular');
      expect(result.filteredTraceData).not.toContain('ResourceSendRequest');
    });

    it('should handle array format trace data', () => {
      const result = reduceTrace(JSON.stringify([{ name: 'Regular' }]), {}); // Use empty options to avoid default filters

      expect(result.stats.originalEventCount).toBe(1);
      expect(result.stats.filteredEventCount).toBe(1);
    });

    it('should throw error for invalid trace data structure', () => {
      expect(() => reduceTrace(JSON.stringify({ invalidField: [] }))).toThrow(
        'traceEvents array not found in the trace file'
      );
    });

    it('should apply default filter options when none provided', () => {
      const result = reduceTrace(
        JSON.stringify({
          traceEvents: [{ name: 'ResourceSendRequest', cat: 'netlog' }],
        })
      );

      expect(result.stats.filteredEventCount).toBe(0); // Network event filtered by default
    });
  });
});

// TypeScript definitions for Chrome DevTools Trace Event format

import { CPUProfile } from './cpuprofile.types';

export type TraceFile = TraceEvent[] | TraceEventContainer;

export interface TraceEventContainer {
  traceEvents: TraceEvent[];
  displayTimeUnit?: 'ms' | 'ns';
  systemTraceEvents?: string;
  metadata?: TraceMetadata;
  stackFrames?: Record<string, StackFrame>;
  samples?: Sample[];
  controllerTraceDataKey?: string;
  powerTraceAsString?: string;
}

/**
 * Top-level metadata for a trace, as found in `.cpuprofile` and DevTools exports.
 */
export interface TraceMetadata {
  source: string; // e.g. "DevTools"
  startTime: string; // ISO timestamp when trace recorded
  hardwareConcurrency?: number; // Number of logical processors
  dataOrigin?: string; // Origin of trace events, e.g. "TraceEvents"
  modifications?: Modifications; // Details of any UI or data modifications
  [key: string]: any; // Allow additional custom metadata
}

/**
 * Modifications made to trace data or UI in DevTools export
 */
export interface Modifications {
  entriesModifications: EntriesModifications;
  initialBreadcrumb: InitialBreadcrumb;
  annotations: Annotations;
}

/** Hidden or expandable entries information */
export interface EntriesModifications {
  hiddenEntries: any[]; // IDs or indexes of hidden entries
  expandableEntries: any[]; // IDs or indexes of expandable entries
}

/** Initial breadcrumb information for time ranges and window */
export interface InitialBreadcrumb {
  window: BreadcrumbWindow;
  child: any | null;
}

/** Time window bounds (min, max) in trace time units (e.g. microseconds) */
export interface BreadcrumbWindow {
  min: number;
  max: number;
  range: number;
}

/**
 * Custom label for a specific trace entry
 */
export interface EntryLabel {
  entryId: number | string; // ID or index of the trace entry
  label: string; // Label text for the entry
  color?: string; // Optional display color for the label
}

/**
 * A time range annotated with a label
 */
export interface LabelledTimeRange {
  startTime: number; // Start timestamp of the range (microseconds)
  endTime: number; // End timestamp of the range (microseconds)
  label: string; // Annotation label for the time range
  color?: string; // Optional display color for the range
}

/**
 * Link or relation between two trace entries
 */
export interface EntryLink {
  fromEntryId: number | string; // Source entry ID for the link
  toEntryId: number | string; // Target entry ID for the link
  linkType?: string; // Optional type or description of the link
}

/** Annotations such as labels and links between entries */
export interface Annotations {
  entryLabels: EntryLabel[]; // Custom labels for entries
  labelledTimeRanges: LabelledTimeRange[]; // Time ranges annotated with labels
  linksBetweenEntries: EntryLink[]; // Links or relations between entries
}

export type Phase =
  // Duration events (begin/end) and complete events:
  | 'B'
  | 'E'
  | 'X'
  // Instant events:
  | 'i'
  // Counter events:
  | 'C'
  // Async events (nestable):
  | 'b'
  | 'n'
  | 'e'
  // Flow events:
  | 's'
  | 't'
  | 'f'
  // Sample events:
  | 'P'
  // Object events:
  | 'N'
  | 'O'
  | 'D'
  // Metadata events:
  | 'M'
  // Memory dump events:
  | 'V'
  | 'v'
  // Mark events:
  | 'R'
  // Clock sync events:
  | 'c'
  // Context events:
  | '('
  | ')'
  // ID linking events:
  | '=';

/** Scope values for instant events (ph='i') */
export type InstantScope = 't' | 'p' | 'g';

/** Category string for trace events */
export type Category<T extends string = string> = `${
  | 'disabled-by-default-'
  | ''}${T}`;

export interface TraceEventBase {
  ph: Phase;
  name?: string;
  cat?: Category;
  pid?: number;
  tid?: number;
  ts: number;
  tts?: number;
  args?: Record<string, any>;
  dur?: number;
  tdur?: number;
  sf?: string | number;
  stack?: Array<string | number>;
  cname?: string;
}

/** Event identifier (for async, object, flow events, etc.) */
export type EventID = string | number;

export interface EventID2 {
  local?: string;
  global?: string;
}

export interface DurationBeginEvent extends TraceEventBase {
  ph: 'B';
  name: string;
}

export interface DurationEndEvent extends TraceEventBase {
  ph: 'E';
}

export interface CompleteEvent extends TraceEventBase {
  ph: 'X';
  name: string;
  dur: number;
  tdur?: number;
}

export interface InstantEvent extends TraceEventBase {
  ph: 'i';
  dur: 0;
  name: string;
  s?: InstantScope;
}

export interface CounterEvent extends TraceEventBase {
  ph: 'C';
  name: string;
  id?: EventID;
  args: Record<string, number>;
}

export interface AsyncBeginEvent extends TraceEventBase {
  ph: 'b';
  name: string;
  id?: EventID;
  id2?: EventID2;
  scope?: string;
}

export interface AsyncInstantEvent extends TraceEventBase {
  ph: 'n';
  name: string;
  id?: EventID;
  id2?: EventID2;
  scope?: string;
}

export interface AsyncEndEvent extends TraceEventBase {
  ph: 'e';
  name?: string;
  id?: EventID;
  id2?: EventID2;
  scope?: string;
}

export interface FlowStartEvent extends TraceEventBase {
  ph: 's';
  name: string;
  id?: EventID;
  id2?: EventID2;
}

export interface FlowStepEvent extends TraceEventBase {
  ph: 't';
  name: string;
  id?: EventID;
  id2?: EventID2;
}

export interface FlowEndEvent extends TraceEventBase {
  ph: 'f';
  name: string;
  id?: EventID;
}

export interface SampleEvent extends TraceEventBase {
  ph: 'P';
  name: string;
  id?: EventID;
}

/**
 * Frame information for TracingStartedInBrowserEvent
 */
export interface Frame {
  frame: string;
  isInPrimaryMainFrame: boolean;
  isOutermostMainFrame: boolean;
  name: string;
  processId: number;
  url: string;
}

/**
 * Event marking that tracing has started in the browser.
 */
export interface TracingStartedInBrowserEvent extends TraceEventBase {
  ph: 'i';
  cat: Category<'devtools.timeline'>;
  name: 'TracingStartedInBrowser';
  pid: number;
  tid: number;
  ts: number;
  s: InstantScope;
  args: {
    data: {
      frameTreeNodeId: number;
      frames: Frame[];
      persistentIds: true;
    };
  };
}

export interface CpuProfilerStartProfilingEvent extends InstantEvent {
  cat: Category<'v8'>;
  name: 'CpuProfiler::StartProfiling';
  ts: number;
}

export interface CpuProfilerStopProfilingEvent extends InstantEvent {
  cat: Category<'v8'>;
  name: 'CpuProfiler::StopProfiling';
  ts: number;
}

export interface ProfileEvent extends SampleEvent {
  cat: Category<'v8.cpu_profiler'>;
  name: 'Profile';
  args: { data: { startTime: number; [key: string]: any } };
}

export interface ProfileChunkEvent extends SampleEvent {
  cat: Category<'v8.cpu_profiler'>;
  name: 'ProfileChunk';
  args: {
    data: {
      cpuProfile: Omit<CPUProfile, 'timeDeltas' | 'startTime' | 'endTime'>;
      timeDeltas?: number[];
      [key: string]: any;
    };
  };
}

export interface ObjectCreatedEvent extends TraceEventBase {
  ph: 'N';
  name: string;
  id: EventID;
}

export interface ObjectSnapshotEvent extends TraceEventBase {
  ph: 'O';
  name: string;
  id: EventID;
  args: { snapshot: any };
}

export interface ObjectDestroyedEvent extends TraceEventBase {
  ph: 'D';
  name: string;
  id: EventID;
}

interface MetadataEventBase extends TraceEventBase {
  ph: 'M';
  cat: '__metadata';
  pid: number;
  tid: number;
}

export interface ProcessNameEvent extends MetadataEventBase {
  name: 'process_name';
  args: { name: string };
}

export interface ThreadNameEvent extends MetadataEventBase {
  name: 'thread_name';
  args: { name: string };
}

export interface GlobalMemoryDumpEvent extends TraceEventBase {
  ph: 'V';
  id: EventID;
  args: Record<string, any>;
}

export interface ProcessMemoryDumpEvent extends TraceEventBase {
  ph: 'v';
  pid: number;
  id: EventID;
  args: Record<string, any>;
}

export interface MarkEvent extends TraceEventBase {
  ph: 'R';
  name: string;
}

export interface ClockSyncEvent extends TraceEventBase {
  ph: 'c';
  name: 'clock_sync';
  args: { sync_id: string; issue_ts?: number };
}

export interface ContextEnterEvent extends TraceEventBase {
  ph: '(';
  name: string;
  id: EventID;
}

export interface ContextLeaveEvent extends TraceEventBase {
  ph: ')';
  name: string;
  id: EventID;
}

export interface IDLinkEvent extends TraceEventBase {
  ph: '=';
  name?: string;
  id: EventID;
  args: { linked_id: EventID };
}

export interface ProcessLabelsEvent extends MetadataEventBase {
  name: 'process_labels';
  args: { labels: string };
}

export interface ProcessSortIndexEvent extends MetadataEventBase {
  name: 'process_sort_index';
  args: { sort_index: number };
}

export interface ThreadSortIndexEvent extends MetadataEventBase {
  name: 'thread_sort_index';
  args: { sort_index: number };
}

export type TraceEvent =
  | DurationBeginEvent
  | DurationEndEvent
  | CompleteEvent
  | InstantEvent
  | CounterEvent
  | AsyncBeginEvent
  | AsyncInstantEvent
  | AsyncEndEvent
  | FlowStartEvent
  | FlowStepEvent
  | FlowEndEvent
  | SampleEvent
  | TracingStartedInBrowserEvent
  | CpuProfilerStartProfilingEvent
  | CpuProfilerStopProfilingEvent
  | ProfileEvent
  | ProfileChunkEvent
  | ObjectCreatedEvent
  | ObjectSnapshotEvent
  | ObjectDestroyedEvent
  | ProcessNameEvent
  | ProcessLabelsEvent
  | ProcessSortIndexEvent
  | ThreadNameEvent
  | ThreadSortIndexEvent
  | GlobalMemoryDumpEvent
  | ProcessMemoryDumpEvent
  | MarkEvent
  | ClockSyncEvent
  | ContextEnterEvent
  | ContextLeaveEvent
  | IDLinkEvent;

export interface StackFrame {
  name?: string;
  category?: string;
  file?: string;
  line?: number;
  column?: number;
  parent?: string;
}

export interface Sample {
  cpu?: number;
  name: string;
  ts: number;
  pid: number;
  tid: number;
  weight?: number;
  sf?: string;
  stack?: string[];
}

import { afterAll, beforeEach, describe, expect, it } from 'vitest';
import {
  execWithCpuProf,
  getCpuProfileName,
  parseCpuProfileName,
} from './utils';
import { join } from 'path';
import { readdir, readFile, rm } from 'fs/promises';
import { execSync } from 'node:child_process';
import { statSync } from 'node:fs';
import { CPUProfile } from './cpuprofile.types';
import { mkdir } from 'node:fs/promises';

const PACKAGE_ROOT = join(__dirname, '..');
const PROJECT_ROOT = join(PACKAGE_ROOT, '../..');
const TMP_DIR = join(PROJECT_ROOT, 'tmp');
const MOCKS_DIR = join(PACKAGE_ROOT, 'mocks');

describe.todo(
  'CPU profile file name generation - This is here to test the official docs and our assumptions',
  () => {
    const profGenDir = join(TMP_DIR, 'generate');
    beforeAll(async () => {
      try {
        if (statSync(profGenDir).isDirectory()) {
          await rm(profGenDir, { recursive: true });
        }
      } catch (e) {}
    });

    afterAll(async () => rm(profGenDir, { recursive: true }));

    it('date in filename is start date (within 1s)', async () => {
      const outDir = join(profGenDir, 'filename-date-is-start-date');
      await mkdir(outDir, { recursive: true });
      const startDate = new Date();
      const profileDur = 2000;

      // Fire off a very short profile so test doesn't actually wait 10s
      expect(() =>
        execSync(
          `node --cpu-prof --cpu-prof-dir=${outDir} -e "setTimeout(() => process.exit(0), ${profileDur})"`
        )
      ).not.toThrow();

      const files = await readdir(outDir);
      expect(files).toHaveLength(1);

      const file = files[0];
      // Filename pattern: CPU.YYYYMMDD.HHMMSS.PID.TID.N.cpuprofile
      const m = file.match(
        /^CPU\.(\d{8})\.(\d{6})\.(\d+)\.(\d+)\.(\d+)\.cpuprofile$/
      );
      expect(m).not.toBeNull();

      const [, datePart, timePart] = m!;

      // Build a Date from the filename parts (using local time):
      //  datePart = "20250510"  YYYY=2025, MM=05, DD=10
      //  timePart = "134625"   hh=13, mm=46, ss=25
      const fileDate = new Date(
        +datePart.slice(0, 4), // year
        +datePart.slice(4, 6) - 1, // monthIndex
        +datePart.slice(6, 8), // day
        +timePart.slice(0, 2), // hour
        +timePart.slice(2, 4), // minute
        +timePart.slice(4, 6) // second
      );

      const profilePath = join(outDir, file);
      const profile: Required<CPUProfile> = JSON.parse(
        (await readFile(profilePath)).toString()
      );
      const profileDurMs = (profile.endTime - profile.startTime) / 1000;

      //Check that the duration is correct
      expect(profileDurMs).toBeGreaterThan(profileDur - 1);
      expect(profileDurMs).toBeLessThanOrEqual(profileDur + 20);

      const deltaFilenameToStartMs = Math.abs(
        fileDate.getTime() - startDate.getTime()
      );

      // Check that the filename date is within 1000ms of the profile start time
      expect(deltaFilenameToStartMs).toBeLessThanOrEqual(1000);
      const stats = statSync(profilePath);

      // check the difference from file birthtimeMs and filename date is close to profile duration
      // this means the date in the file name is younger than the file creating date
      expect(stats.birthtimeMs - fileDate.getTime()).toBeGreaterThanOrEqual(
        profileDur
      );
      expect(stats.birthtimeMs - fileDate.getTime()).toBeLessThan(
        profileDur + 1000
      );
    });
  }
);

describe('getCpuProfileName', () => {
  const sequenceMap = new Map();
  const testDate = new Date(2025, 4, 10, 13, 46, 25); // May 10, 2025, 13:46:25

  beforeEach(() => {
    sequenceMap.clear();
  });

  it('should create a CPU profile name in format PREFIX.YYYYMMDD.HHMMSS.PID.TID.SEQ.EXT', async () => {
    expect(
      getCpuProfileName(
        {
          prefix: 'CPU',
          pid: 51430,
          tid: 0,
          date: testDate,
        },
        sequenceMap
      )
    ).toBe('CPU.20250510.134625.51430.0.001.cpuprofile');
  });

  it('should increment sequence number for same PID-TID combination', () => {
    expect(
      getCpuProfileName({ pid: 12345, tid: 0, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12345.0.001.cpuprofile');
    expect(
      getCpuProfileName({ pid: 12345, tid: 0, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12345.0.002.cpuprofile');
    expect(
      getCpuProfileName({ pid: 12345, tid: 0, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12345.0.003.cpuprofile');
  });

  it('should not increment sequence number for different PID combination', () => {
    expect(
      getCpuProfileName({ pid: 12345, tid: 0, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12345.0.001.cpuprofile');
    expect(
      getCpuProfileName({ pid: 12346, tid: 0, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12346.0.001.cpuprofile');
    expect(
      getCpuProfileName({ pid: 12347, tid: 0, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12347.0.001.cpuprofile');
  });

  it('should not increment sequence number for different TID combination', () => {
    expect(
      getCpuProfileName({ pid: 12345, tid: 1, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12345.1.001.cpuprofile');
    expect(
      getCpuProfileName({ pid: 12345, tid: 2, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12345.2.001.cpuprofile');
    expect(
      getCpuProfileName({ pid: 12345, tid: 3, date: testDate }, sequenceMap)
    ).toBe('CPU.20250510.134625.12345.3.001.cpuprofile');
  });

  it('should support custom file extensions', () => {
    const customExt = getCpuProfileName(
      {
        pid: 12345,
        date: testDate,
        extension: 'profile',
      },
      sequenceMap
    );
    expect(customExt).toBe('CPU.20250510.134625.12345.0.001.profile');
  });

  it('should support custom prefix extensions', () => {
    const customExt = getCpuProfileName(
      {
        pid: 12345,
        date: testDate,
        prefix: 'PROF',
      },
      sequenceMap
    );
    expect(customExt).toBe('PROF.20250510.134625.12345.0.001.cpuprofile');
  });
});

describe('parseCpuProfileName', () => {
  const VALID_PROFILE_NAME = 'CPU.20250510.134625.12345.0.001.cpuprofile';

  it('should parse prefix of standard CPU profile name', () => {
    expect(parseCpuProfileName(VALID_PROFILE_NAME)).toStrictEqual(
      expect.objectContaining({ prefix: 'CPU' })
    );
  });

  it('should parse date of standard CPU profile name', () => {
    const expected = new Date(2025, 4, 10, 13, 46, 25); // May 10, 2025, 13:46:25
    expect(parseCpuProfileName(VALID_PROFILE_NAME)).toStrictEqual(
      expect.objectContaining({ date: expected })
    );
  });

  it('should parse PID of standard CPU profile name', () => {
    expect(parseCpuProfileName(VALID_PROFILE_NAME)).toStrictEqual(
      expect.objectContaining({ pid: 12345 })
    );
  });

  it('should parse TID of standard CPU profile name', () => {
    expect(parseCpuProfileName(VALID_PROFILE_NAME)).toStrictEqual(
      expect.objectContaining({ tid: 0 })
    );
  });

  it('should parse sequence of standard CPU profile name', () => {
    expect(parseCpuProfileName(VALID_PROFILE_NAME)).toStrictEqual(
      expect.objectContaining({ seq: 1 })
    );
  });

  it('should parse extension of standard CPU profile name', () => {
    expect(parseCpuProfileName(VALID_PROFILE_NAME)).toStrictEqual(
      expect.objectContaining({ extension: 'cpuprofile' })
    );
  });

  it('should throw error for malformed profile name', () => {
    expect(() => parseCpuProfileName('invalid.profile.name')).toThrow();
  });
});

describe('execWithCpuProf', () => {
  const mergeTmp = join(TMP_DIR, 'cpu-profiling/merge');

  beforeEach(async () => {
    await rm(mergeTmp, { recursive: true, force: true });
  });

  afterAll(async () => {
    await rm(mergeTmp, { recursive: true, force: true });
  });

  it.each([
    ['fork', join(MOCKS_DIR, 'program', 'fork-children.mjs')],
    ['spawn', join(MOCKS_DIR, 'program', 'spawn-children.mjs')],
    ['worker', join(MOCKS_DIR, 'program', 'worker-children.mjs')],
  ])(
    'should create CPU profiles for %s processes',
    async (caseName, scriptPath) => {
      const testCaseDir = join(mergeTmp, caseName);

      const result = await execWithCpuProf({
        scriptPath,
        outputDir: testCaseDir,
        cpuProfOptions: {
          interval: 100,
        },
      });

      expect(result.stdout).toContain('PID');
      expect(result.stderr).toBe('');

      const files = await readdir(testCaseDir);
      const cpuProfiles = files.filter(
        (f) => f.startsWith('CPU.') && f.endsWith('.cpuprofile')
      );
      expect(cpuProfiles).toHaveLength(3);

      const profilePath = join(testCaseDir, cpuProfiles[0]);
      const profileContent = await readFile(profilePath, 'utf8');
      const profile: CPUProfile = JSON.parse(profileContent);

      expect(profile).toMatchObject({
        nodes: expect.any(Array),
        samples: expect.any(Array),
        timeDeltas: expect.any(Array),
        startTime: expect.any(Number),
        endTime: expect.any(Number),
      });

      // Ensure timeDeltas exists before calculating average
      expect(profile.timeDeltas).toBeDefined();
      expect(profile.timeDeltas!.length).toBeGreaterThan(0);
    }
  );

  it('should respect custom CPU profiling options', async () => {
    const testCaseDir = join(mergeTmp, 'custom-options');

    await execWithCpuProf({
      scriptPath: '-e "console.log(\'Hello, world!\')"',
      outputDir: testCaseDir,
      cpuProfOptions: {
        interval: 500,
        name: 'custom-profile.cpuprofile',
      },
    });

    const files = await readdir(testCaseDir);
    expect(files).toContain('custom-profile.cpuprofile');
  });

  it('should create output directory if it does not exist', async () => {
    const testCaseDir = join(mergeTmp, 'new-dir/subdir');

    await execWithCpuProf({
      scriptPath: '-e "console.log(\'Hello, world!\')"',
      outputDir: testCaseDir,
    });

    const files = await readdir(testCaseDir);
    expect(files.length).toBeGreaterThan(0);
  });

  it('should throw on invalid script path', async () => {
    await expect(
      execWithCpuProf({
        scriptPath: 'non-existent.js',
        outputDir: mergeTmp,
      })
    ).rejects.toThrow();
  });

  it('should throw on timeout', async () => {
    const scriptPath = join(MOCKS_DIR, 'program', 'fork-children.mjs');

    await expect(
      execWithCpuProf({
        scriptPath,
        outputDir: mergeTmp,
        timeoutMs: 1,
      })
    ).rejects.toThrow();
  });

  it('should handle disabled profiling', async () => {
    const testCaseDir = join(mergeTmp, 'disabled');

    await execWithCpuProf({
      scriptPath: '-e "console.log(\'Hello, world!\')"',
      outputDir: testCaseDir,
      cpuProfOptions: {
        enabled: false,
      },
    });

    const files = await readdir(testCaseDir);
    expect(files.filter((f) => f.endsWith('.cpuprofile'))).toHaveLength(0);
  });
});

import { exec } from 'child_process';
import { mkdir, rm } from 'fs/promises';
import { promisify } from 'util';
import { TraceEvent } from './traceprofile.types';

const execAsync = promisify(exec);

export type CpuProfileNameOptions = {
  prefix?: string;
  pid?: number;
  tid?: number;
  date?: Date;
  extension?: string;
  seq?: number;
};

const cpuProfileSeqMap = new Map();

/**
 * Generates a CPU profile filename like:
 * PREFIX.YYYYMMDD.HHMMSS.PID.TID.SEQ.cpuprofile
 */
export function getCpuProfileName(
  {
    prefix = 'CPU',
    pid,
    tid = 0,
    date = new Date(),
    extension = 'cpuprofile',
  }: CpuProfileNameOptions,
  sequenceMap = cpuProfileSeqMap
) {
  const pad = (n: number, width = 2) => String(n).padStart(width, '0');

  // Build date/time segments
  const year = date.getFullYear();
  const month = pad(date.getMonth() + 1);
  const day = pad(date.getDate());
  const hours = pad(date.getHours());
  const minutes = pad(date.getMinutes());
  const seconds = pad(date.getSeconds());

  const datePart = `${year}${month}${day}`;
  const timePart = `${hours}${minutes}${seconds}`;

  // Get and increment the sequence for this PID-TID
  const key = `${pid}-${tid}`;
  const currentSeq = sequenceMap.get(key) || 0;
  const nextSeq = currentSeq + 1;
  sequenceMap.set(key, nextSeq);
  const seqPart = pad(nextSeq, 3);

  // Remove leading dot from extension if present
  const cleanExtension = extension.startsWith('.')
    ? extension.slice(1)
    : extension;

  return `${prefix}.${datePart}.${timePart}.${pid}.${tid}.${seqPart}.${cleanExtension}`;
}

/**
 * Parses a CPU profile filename and extracts its components.
 *
 * @param {string} file - Filename in the format:
 *   {prefix}.{YYYYMMDD}.{HHMMSS}.{pid}.{tid}.{seq}.{extension}
 * @returns {object} Parsed details including:
 *   prefix, pid, tid, seq, date (Date object), extension, isMain
 * @throws {Error} If the filename doesn't match the expected pattern.
 *
 * @example
 * const info = parseCpuProfileName('CPU.20250510.134625.51430.1.1.cpuprofile');
 *  info = {
 *    prefix: 'CPU',
 *    pid: 51430,
 *    tid: 1,
 *    seq: 1,
 *    date: Date('2025-05-10T13:46:25'),
 *    extension: 'cpuprofile',
 *    isMain: true
 *  }
 */
export function parseCpuProfileName(
  file: string
): Required<Omit<CpuProfileNameOptions, 'prefix' | 'extension'>> &
  Pick<CpuProfileNameOptions, 'prefix' | 'extension'> {
  const pattern =
    /^(?<prefix>[^.]+)\.(?<ymd>\d{8})\.(?<hms>\d{6})\.(?<pid>\d+)\.(?<tid>\d+)\.(?<seq>\d+)(?:\.(?<ext>.*))?$/;
  const match = file.match(pattern);
  if (!match?.groups) {
    throw new Error(`Invalid CPU profile filename: ${file}`);
  }

  const {
    prefix,
    ymd,
    hms,
    pid = 0,
    tid = 0,
    seq,
    ext: extension = '',
  } = match.groups;

  const year = +ymd.slice(0, 4);
  const month = +ymd.slice(4, 6) - 1;
  const day = +ymd.slice(6, 8);
  const hours = +hms.slice(0, 2);
  const minutes = +hms.slice(2, 4);
  const seconds = +hms.slice(4, 6);

  return {
    prefix,
    pid: Number(pid),
    tid: Number(tid),
    seq: Number(seq),
    date: new Date(year, month, day, hours, minutes, seconds),
    extension,
  };
}

export interface CpuProfOptions {
  /** Enable the V8 CPU profiler */
  enabled?: boolean; // default: true

  /** Directory to write .cpuprofile files */
  dir?: string;

  /** Filename pattern (supports %P, %T, %D, %H) */
  name?: string;

  /** Sampling interval in microseconds (default: 1000) */
  interval?: number;
}

export interface ExecWithCpuProfConfig {
  scriptPath: string;
  outputDir: string;
  timeoutMs?: number;
  cpuProfOptions?: CpuProfOptions;
  sampleProfInterval?: string;
}

export async function execWithCpuProf(
  config: ExecWithCpuProfConfig
): Promise<{ stdout: string; stderr: string }> {
  const {
    scriptPath,
    outputDir,
    timeoutMs = 5000,
    cpuProfOptions = {},
    sampleProfInterval,
  } = config;

  const {
    enabled = true,
    interval = 1000,
    dir = outputDir,
    name,
  } = cpuProfOptions;

  // Prepare output directory
  await rm(dir, { recursive: true, force: true });
  await mkdir(dir, { recursive: true });

  // Build profiling flags
  const profFlags: string[] = [];

  if (enabled) {
    profFlags.push('--cpu-prof');
    if (interval) profFlags.push(`--cpu-prof-interval=${interval}`);
    if (dir) profFlags.push(`--cpu-prof-dir="${dir}"`);
    if (name) profFlags.push(`--cpu-prof-name="${name}"`);
  }

  if (sampleProfInterval) {
    profFlags.push(`--sample-prof-interval=${sampleProfInterval}`);
  }

  // Handle -e flag differently than file paths
  const isEvalScript = scriptPath.startsWith('-e');
  const formattedScript = isEvalScript
    ? scriptPath // Keep the -e command exactly as provided
    : `"${scriptPath}"`;

  const command = `node ${profFlags.join(' ')} ${formattedScript}`;

  try {
    const result = await execAsync(
      command,
      timeoutMs ? { timeout: timeoutMs } : {}
    );
    return result;
  } catch (error: any) {
    throw new Error(`CPU profile execution failed: ${error.message || error}`);
  }
}

export function microsecondsToDate(microseconds: number): Date {
  return new Date(microseconds / 1000);
}

/**
 * Helper function to coerce string array arguments from CLI input
 */
export function coerceStringArray(
  arg: string[] | string | boolean
): string[] | undefined {
  if (arg === false || arg === undefined) {
    return undefined;
  }
  if (typeof arg === 'string') {
    return arg.split(',').map((item: string) => item.trim());
  }
  if (Array.isArray(arg)) {
    return arg.flatMap((item: string) =>
      item.split(',').map((s: string) => s.trim())
    );
  }
  return undefined;
}

/**
 * Helper function to coerce number array arguments from CLI input
 */
export function coerceNumberArray(
  arg: string[] | string | boolean,
  type: 'PID' | 'TID'
): number[] | undefined {
  if (arg === false || arg === undefined) {
    return undefined;
  }
  const values =
    typeof arg === 'string'
      ? arg.split(',')
      : Array.isArray(arg)
      ? arg.flatMap((item: string) => item.split(','))
      : [];
  return values.map((id: string) => {
    const numId = parseInt(id.trim(), 10);
    if (isNaN(numId)) {
      throw new Error(`Invalid ${type}: ${id}. ${type}s must be numbers.`);
    }
    return numId;
  });
}

/**
 * Helper function to coerce string array arguments with default values
 */
export function coerceStringArrayWithDefaults(
  arg: string[] | string | boolean,
  defaults: string[] = []
): string[] {
  // Handle yargs negation (--no-exclude-*)
  if (arg === false || arg === undefined) {
    return [];
  }

  if (typeof arg === 'string') {
    const userValues = arg.split(',').map((item: string) => item.trim());
    return [...new Set([...defaults, ...userValues])];
  }
  if (Array.isArray(arg)) {
    const userValues = arg.flatMap((item: string) =>
      item.split(',').map((s: string) => s.trim())
    );
    return [...new Set([...defaults, ...userValues])];
  }
  return defaults;
}

export function sortTraceEvents(rawEvents: TraceEvent[]): TraceEvent[] {
  const metaOnly = rawEvents.filter((e) => e.ph === 'M');
  const eventsOnly = rawEvents.filter((e) => e.ph !== 'M');
  metaOnly.sort((a, b) => a.ts - b.ts);
  eventsOnly.sort((a, b) => a.ts - b.ts);
  return [...metaOnly, ...eventsOnly];
}
